import React, { useEffect, useState } from "react";
import "./components/style.css";
import { AnimeList } from "./components/AnimeList";
import { animeInfo } from "./components/animeInfo";

function App() {
  const [search, setSearch] = useState("Naruto");
  const [animeData, setAnimeData] = useState();
  const [animeInfo, setAnimeInfo] = useState();

  const getData = async () => {
    const response = await fetch(`https://api.jikan.moe/v4/anime?q=${search}&limit=20`);
    const result = await response.json();
    setAnimeData(result.data);
  };

  useEffect(() => {
    getData();
  }, [search]);

  return (
    <>
      <div className="header">
        <h1>MyAnimeList</h1>
        <div className="search-box">
          <input
            type="search"
            placeholder="Search your anime"
            onChange={(e) => setSearch(e.target.value)}
          />
        </div>
      </div>
      <div className="container">
        <div className="animeInfo">{animeInfo && <animeInfo></animeInfo>}</div>
        <div className="anime-row">
          <h2 className="text-heading">Anime</h2>
          <div className="row">
            <AnimeList animeList={animeData} setAnimeInfo={setAnimeInfo} />
          </div>
        </div>
      </div>
    </>
  );
}

export default App;
